    private void view_det_btnActionPerformed(java.awt.event.ActionEvent evt) {                                             

        m1 = Integer.valueOf(mark_1_text.getText()).intValue();
        m2 = Integer.valueOf(mark_2_text.getText()).intValue();
        m3 = Integer.valueOf(mark_3_text.getText()).intValue();
        per = (m1 + m2 +m3)/3;
        if(m1 >= 35 && m2 >= 35 && m3 >= 35){
            if (per >= 90) {
                grd = "O";
            }
            else if(per >=80 && per<=90){
                grd = "A";
            }
            else if(per >=70 && per<=80){
                grd = "B";
            }
            else if(per >=60 && per<=70){
                grd = "C";
            }
            else if(per >=50 && per<=60){
                grd = "D";
            }
            else{
                grd = "E";
            }
        }
        else
        {
            per = 0;
            grd = "Fail";
        }
        int count = tableModel.getRowCount()+1;
        tableModel.addRow(new Object[]{new Integer(roll_no_text.getText()), s_name_text.getText(), new Float(mark_1_text.getText()), new Float(mark_2_text.getText()), new Float(mark_3_text.getText()), new Float(per)+"%", grd});

        roll_no_text.setText("");
        s_name_text.setText("");
        mark_1_text.setText("");
        mark_2_text.setText("");
        mark_3_text.setText("");
    }                                            

